## funciones


extraer_texto_parentesis <- function(vec) {
  stringr::str_extract(vec, "\\([^()]+\\)$")
}

extraer_numeros_parentesis <- function(texto) {
 texto <-  str_extract(texto, 
                           "(?<=\\()[^()]+(?=\\))") |>
    str_replace_all("[()]", "") |>
   str_extract("^[^\\.\\s-]+")
  
  return(texto)
}



contar_palabras <- function(texto) {
  stri_count_words(texto)
}

seleccionar_ultimas_n <- function(texto) {
  tryCatch({
    num_palabras <- contar_palabras(texto)
    contiene_numeros <- str_detect(texto, "\\d")  # 游댌 Detecta si hay al menos un n칰mero
    
    if (!contiene_numeros) {
      return(texto)  # 游 Si no hay n칰meros, devuelve el texto completo
    }
    
    n <- ifelse(num_palabras >= 6, 5, 3)  # 游댳 Define `n` seg칰n la cantidad de palabras
    
    palabras <- unlist(str_split(texto, "\\s+"))  # 游댳 Divide el texto en palabras
    
    if (length(palabras) >= n) {
      paste(tail(palabras, n), collapse = " ")  # 游댳 Toma las 칰ltimas `n` palabras
    } else {
      texto  # 游댳 Devuelve el texto original si tiene menos palabras que `n`
    }
  }, error = function(e) texto)  # 游댳 En caso de error, devuelve el texto original
}




limpiar_texto <- function(texto) {
  
  ## correci칩n de texto 
  texto <- gsub("^[^A-Za-z]+", "", texto)
  
  texto <- gsub("\\s*\\(.*?\\)", " ", texto) |> str_squish()
  
  texto <- gsub("\\b[A-Z]\\b", "", texto) |> str_squish()
  
  texto <- gsub("\\bUND\\b|\\b\\S*\\?\\S*\\b", "", texto) |> str_squish()
  
  return(texto)
}

## estandarizar texto

estandarizar_texto <- function(texto) {
  
  texto <- str_replace_all(texto, "(?<=\\p{L})\\.(?=\\p{L})", " ")
  
  ## comillas
  texto <- chartr("츼칄칈칍칔칖치칠칤칩칰칲침", "AEIOUUaeiouun", texto)
  
  return(texto)
}

## implementar 

# Funci칩n para encontrar la mejor coincidencia dentro del umbral



encontrar_mejor_coincidencia <- function(texto, 
                                         candidatos, 
                                         peso_jaccard = 0.3, 
                                         peso_lcs = 0.5, 
                                         peso_jw = 0.2, 
                                         percentil_umbral = 0.1,  # Umbral din치mico (percentil 10%)
                                         umbral_minimo = 0.42) {  # Umbral fijo m칤nimo
  
  
  # Calcular las distancias para todos los candidatos
  dist_jaccard <- stringdist(texto, candidatos, method = "jaccard")
  dist_lcs <- stringdist(texto, candidatos, method = "lcs")
  dist_jw <- stringdist(texto, candidatos, method = "jw")

  # Normalizar cada distancia
  dist_jaccard <- dist_jaccard / max(dist_jaccard, na.rm = TRUE)
  dist_lcs <- dist_lcs / max(dist_lcs, na.rm = TRUE)
  dist_jw <- dist_jw / max(dist_jw, na.rm = TRUE)
  
  # Calcular la distancia combinada con pesos
  dist_combinada <- (peso_jaccard * dist_jaccard) + 
    (peso_lcs * dist_lcs) + 
    (peso_jw * dist_jw)
  
  # Definir el umbral din치mico basado en el percentil
  umbral_dinamico <- quantile(dist_combinada, percentil_umbral, na.rm = TRUE)
  
  # Elegir el umbral final (el menor entre el din치mico y el m칤nimo predefinido)
  umbral_final <- min(umbral_dinamico, umbral_minimo)
  
  # Filtrar solo las coincidencias dentro del umbral
  dentro_del_umbral <- which(dist_combinada <= umbral_final)
  
  if (length(dentro_del_umbral) == 0) {
    return(NA)  # Si no hay coincidencias dentro del umbral, devolver NA
  }
  
  # Seleccionar la mejor coincidencia (m칤nima distancia combinada)
  mejor_indice <- dentro_del_umbral[which.min(dist_combinada[dentro_del_umbral])]
  
  return(candidatos[mejor_indice])
}

